import React, { useState } from 'react';
import axios from 'axios';

interface LoginFormProps {
  onSuccess: (token: string) => void;
}

const LoginForm: React.FC<LoginFormProps> = ({ onSuccess }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await axios.post('/api/v1/auth/login', {
        email,
        password,
      });

      localStorage.setItem('access_token', response.data.tokens.access_token);
      localStorage.setItem('refresh_token', response.data.tokens.refresh_token);
      onSuccess(response.data.tokens.access_token);
    } catch (err: any) {
      setError(err.response?.data?.error || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className='space-y-4'>
      {error && <div className='text-red-600'>{error}</div>}
      <input
        type='email'
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder='Email'
        className='w-full px-4 py-2 border rounded'
        required
      />
      <input
        type='password'
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder='Password'
        className='w-full px-4 py-2 border rounded'
        required
      />
      <button
        type='submit'
        disabled={loading}
        className='w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 disabled:opacity-50'
      >
        {loading ? 'Logging in...' : 'Login'}
      </button>
    </form>
  );
};

export default LoginForm;

bonds_list_component.code:
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BondCard from './BondCard';

interface Bond {
  _id: string;
  bond_id: string;
  name: string;
  coupon_rate: number;
  maturity_date: string;
  risk_category: string;
  expected_returns: number;
}

const BondsList: React.FC = () => {
  const [bonds, setBonds] = useState<Bond[]>([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({ risk: 'all', maturity: 'all' });

  useEffect(() => {
    fetchBonds();
  }, [filters]);

  const fetchBonds = async () => {
    try {
      const params = new URLSearchParams();
      if (filters.risk !== 'all') params.append('risk', filters.risk);
      if (filters.maturity !== 'all') params.append('maturity', filters.maturity);

      const response = await axios.get(`/api/v1/bonds?${params}`);
      setBonds(response.data.data.bonds);
    } catch (error) {
      console.error('Failed to fetch bonds:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className='p-6'>
      <div className='mb-6 flex gap-4'>
        <select
          value={filters.risk}
          onChange={(e) => setFilters({ ...filters, risk: e.target.value })}
          className='px-4 py-2 border rounded'
        >
          <option value='all'>All Risk Levels</option>
          <option value='low'>Low Risk</option>
          <option value='medium'>Medium Risk</option>
          <option value='high'>High Risk</option>
        </select>
        <select
          value={filters.maturity}
          onChange={(e) => setFilters({ ...filters, maturity: e.target.value })}
          className='px-4 py-2 border rounded'
        >
          <option value='all'>All Maturities</option>
          <option value='1y'>1 Year</option>
          <option value='5y'>5 Years</option>
          <option value='10y'>10 Years</option>
        </select>
      </div>
      {loading ? (
        <div>Loading bonds...</div>
      ) : (
        <div className='grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4'>
          {bonds.map((bond) => (
            <BondCard key={bond._id} bond={bond} />
          ))}
        </div>
      )}
    </div>
  );
};

export default BondsList;
